attrs.script:
#install the NuGet package provider so that we can install modules from the PowerShell Gallery
try {$result = Install-PackageProvider -Name NuGet -MinimumVersion 2.8.5.201 -Force -ErrorAction Stop}
catch {throw "Error installing package NuGet : $($_.Exception.Message)"}
write-host "Package $($result.name):$($result.version) was successfully installed" -ForegroundColor Green

#trust the Windows PowerShell Gallery repository
try {$result = Set-PSRepository -Name "PSGallery" -InstallationPolicy Trusted -ErrorAction Stop}
catch {throw "Error trusting the PowerShell Gallery repository : $($_.Exception.Message)"}
write-host "Now trusting PowerShell Gallery repository" -ForegroundColor Green
$Error.Clear() #required as PoSH populates $error even though the cmdlet completed successfully

#install the Windows Update PowerShell module
try {$result = Install-Module PSWindowsUpdate -Confirm:$false -Force -ErrorAction Stop}
catch {throw "Error installing the PSWindowsUpdate module from the PowerShell Gallery: $($_.Exception.Message)"}
write-host "Successfully installed the PSWindowsUpdate module from the PowerShell Gallery" -ForegroundColor Green

#enabling Windows Update remoting
try {$result = Enable-WURemoting -Verbose -ErrorAction Stop}
catch {throw "Error enabling Windows Update remoting: $($_.Exception.Message)"}
write-host "Successfully enabled Windows Update remoting" -ForegroundColor Green

$Error.Clear() #required as PoSH populates $error even though the cmdlet completed successfully

#schedule an installation of all applicable patches 2 minutes from now and automatically reboot. Do this for 3 reboot cycles (after each cycle, updates will be checked again and installed if required)
try {Get-WUInstall -AcceptAll -Verbose -Install -AutoReboot -RecurseCycle 3 -ScheduleJob ((Get-Date).Addminutes(2)) -PSWUSettings @{SmtpServer="@@{smtp_server}@@";From="@@{wu_rpt_from}@@";To="@@{wu_rpt_recipient}@@";Port=@@{smtp_port}@@} -ErrorAction Stop}
catch {throw "Error scheduling Windows Update patch installation : $($_.Exception.Message)"}
write-host "Successfully scheduled Windows Update patch installation." -ForegroundColor Green

#display the scheduled job
try {Get-WUJob -ErrorAction Stop}
catch {throw "Error displaying Windows Update job : $($_.Exception.Message)"}

#display details fo the scheduled task
try {Get-ScheduledTask -TaskName PSWindowsUpdate -ErrorAction Stop | Select -Property TaskName,Date,State}
catch {throw "Error displaying Windows Update scheduled task details: $($_.Exception.Message)"}

#display scheduled task detailed action
try{(Get-ScheduledTask -TaskName PSWindowsUpdate -ErrorAction Stop).Actions}
catch {throw "Error displaying Windows Update scheduled task action details : $($_.Exception.Message)"}

try {Set-ExecutionPolicy Unrestricted -Force -Confirm:$false -ErrorAction Stop}
catch {throw "Error setting Powershell execution policy to unrestricted : $($_.Exception.Message)"}
write-host "Successfully set Powershell execution policy to unrestricted." -ForegroundColor Green

iex ((New-Object System.Net.WebClient).DownloadString('https://chocolatey.org/install.ps1'))

#check which updates are still detected as applicable
Get-WUInstall -Verbose

#display details fo the scheduled task
try {Get-ScheduledTask -TaskName PSWindowsUpdate -ErrorAction Stop | Select -Property TaskName,Date,State}
catch {throw "Error displaying Windows Update scheduled task details: $($_.Exception.Message)"}

#display scheduled task detailed action
try{(Get-ScheduledTask -TaskName PSWindowsUpdate -ErrorAction Stop).Actions}
catch {throw "Error displaying Windows Update scheduled task action details : $($_.Exception.Message)"}
